INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES
(2, 2, 'OC Search Category by inspire', 'search_category', 'inspire', '3.0', 'http://www.inspire.com/', '<modification>\r\n    <name>OC Search Category by inspire</name>\r\n	<version>3.0</version>\r\n	<link>http://www.inspire.com/</link>\r\n	<author>inspire</author>\r\n	<code>search_category</code>\r\n\r\n	<file path=\"catalog/controller/common/header.php\">\r\n		<operation>\r\n			<search ><![CDATA[$data[\'search\'] = $this->load->controller(\'common/search\');]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n				/* Edit for Search Category Module by OCMod */\r\n				$module_status = $this->config->get(\'module_ocsearchcategory_status\');\r\n				if($module_status) {\r\n					$data[\'search\'] = $this->load->controller(\'extension/module/ocsearchcategory\');\r\n				} else {\r\n					$data[\'search\'] = $this->load->controller(\'common/search\');\r\n				}\r\n				/* End Code */\r\n			]]></add>\r\n		</operation>\r\n	</file>	\r\n</modification>', 1, '2018-06-01 20:40:43'),
(3, 3, 'Inspire QuickView', 'inspirequickview', 'Inspirethemes', '1.0.0', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Inspire QuickView</name>\n	<code>inspirequickview</code>\n	<version>1.0.0</version>\n	<author>Inspirethemes</author>\n	<file path=\"catalog/controller/common/header.php\">\n        <operation>\n            <search><![CDATA[$data[\'title\'] = $this->document->getTitle();]]></search>\n            <add position=\"before\">\n                <![CDATA[\n               $data[\'quickview\'] = $this->load->controller(\'extension/module/inspirequickview\');\n            ]]>\n            </add>\n        </operation>     \n    </file>\n</modification>', 1, '2018-06-03 20:55:55'),
(4, 4, 'Inspire Newsletter', 'inspirenewsletter', 'inspirethemes', '1.0.0', 'http://www.inspirethemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Inspire Newsletter</name>\n	<code>inspirenewsletter</code>\n	<version>1.0.0</version>\n	<author>inspirethemes</author>\n	<link>http://www.inspirethemes.com</link>\n</modification>', 1, '2018-06-06 21:45:41');
