(47, 0, 'default', 'product/product', '{{ header }}\n&lt;div id=&quot;product-product&quot; class=&quot;container common-shed&quot;&gt;\n  &lt;div class=&quot;row&quot;&gt;{{ column_left }}\n    {% if column_left and column_right %}\n    {% set class = \'col-sm-5\' %}\n    {% elseif column_left or column_right %}\n    {% set class = \'col-xs-12 col-sm-8 col-lg-9 col-md-9\' %}\n    {% else %}\n    {% set class = \'col-sm-12\' %}\n    {% endif %}\n    &lt;div id=&quot;content&quot; class=&quot;{{ class }}&quot;&gt;{{ content_top }}\n       &lt;ul class=&quot;breadcrumb&quot;&gt;\n        {% for breadcrumb in breadcrumbs %}\n        &lt;li&gt;&lt;a href=&quot;{{ breadcrumb.href }}&quot;&gt;{{ breadcrumb.text }}&lt;/a&gt;&lt;/li&gt;\n        {% endfor %}\n      &lt;/ul&gt;\n      &lt;div class=&quot;row&quot;&gt;\n        {% if column_left or column_right %}\n        {% set class = \'col-lg-6 col-md-6 col-xs-12\' %}\n        {% else %}\n        {% set class = \'col-md-5 col-sm-6 proimg\' %}\n        {% endif %}\n        &lt;div class=&quot;{{ class }}&quot;&gt; \n        {% if thumb or images %}\n                        &lt;ul class=&quot;thumbnails&quot;&gt;\n                            {% if thumb %}\n                            &lt;li&gt;&lt;a class=&quot;thumbnail&quot; href=&quot;{{ popup }}&quot; title=&quot;{{ heading_title }}&quot;&gt;\n                                    &lt;img data-zoom-image=&quot;{{ popup }}&quot; src=&quot;{{ thumb }}&quot; id=&quot;inspzoom&quot; class=&quot;img-responsive center-block&quot; alt=&quot;image&quot;&gt;\n                                &lt;/a&gt;\n                            &lt;/li&gt;\n                            {% endif %}\n                            {% if images %}\n                            &lt;div class=&quot;row addoinalrow&quot;&gt;\n                            &lt;li id=&quot;additional&quot; class=&quot;owl-carousel&quot;&gt;\n                                {% for image in images %}\n                          