price&quot;&gt;\n              {% if discount %}\n                    &lt;span&gt;{{ discount }} &amp;nbsp; &lt;/span&gt; \n                    &lt;span style=&quot;color: #999;text-decoration: line-through;&quot;&gt; {{ price }}&lt;/span&gt;\n                {% else %} \n                    {{ price }} \n                {% endif %} \n              &lt;/h2&gt;\n            &lt;/li&gt;\n            {% else %}\n            &lt;!-- &lt;li&gt;&lt;span style=&quot;text-decoration: line-through;&quot;&gt;{{ price }}&lt;/span&gt;&lt;/li&gt; --&gt;\n            &lt;li class=&quot;text-prodecor-bold&quot;&gt;\n              &lt;h2&gt;{{ special }}&lt;/h2&gt;\n            &lt;/li&gt;\n            {% endif %}\n            &lt;!-- {% if tax %}\n            &lt;li&gt;{{ text_tax }} {{ tax }}&lt;/li&gt;\n            {% endif %} --&gt;\n            {% if points %}\n            &lt;li class=&quot;text-prodecor-bold&quot;&gt;{{ text_points }} {{ points }}&lt;/li&gt;\n            {% endif %}\n            {% if discounts %}\n            &lt;li&gt;\n              &lt;hr class=&quot;prosp&quot;&gt;\n            &lt;/li&gt;\n            {% for discount in discounts %}\n            &lt;li&gt;{{ discount.quantity }}{{ text_discount }}{{ discount.price }}&lt;/li&gt;\n            {% endfor %}\n            {% endif %}\n          &lt;/ul&gt;\n          {% endif %}\n          &lt;div id=&quot;product&quot;&gt; {% if options %}\n            &lt;hr class=&quot;prosp&quot;&gt;\n            &lt;h3&gt;{{ text_option }}&lt;/h3&gt;\n            {% for option in options %}\n            {% if option.type == \'select\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-option{{ option.product_option_id }}&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;select name=&quot;option[{{ option.product_option_id }}]&quot; id=&quot;input-option{{ option.product_op