(49, 0, 'default', 'product/search', '{{ header }}\n&lt;div id=&quot;product-search&quot; class=&quot;container&quot;&gt;\n  &lt;ul class=&quot;breadcrumb&quot;&gt;\n    {% for breadcrumb in breadcrumbs %}\n    &lt;li&gt;&lt;a href=&quot;{{ breadcrumb.href }}&quot;&gt;{{ breadcrumb.text }}&lt;/a&gt;&lt;/li&gt;\n    {% endfor %}\n  &lt;/ul&gt;\n  &lt;div class=&quot;row&quot;&gt;{{ column_left }}\n    {% if column_left and column_right %}\n    {% set class = \'col-sm-6\' %}\n    {% elseif column_left or column_right %}\n    {% set class = \'col-sm-9\' %}\n    {% else %}\n    {% set class = \'col-sm-12\' %}\n    {% endif %}\n    &lt;div id=&quot;content&quot; class=&quot;{{ class }}&quot;&gt;{{ content_top }}\n      &lt;h1 class=&quot;heading&quot;&gt;{{ heading_title }}&lt;/h1&gt;\n      &lt;label class=&quot;control-label&quot; for=&quot;input-search&quot;&gt;{{ entry_search }}&lt;/label&gt;\n      &lt;div class=&quot;row&quot;&gt;\n        &lt;div class=&quot;col-sm-4&quot;&gt;\n          &lt;input type=&quot;text&quot; name=&quot;search&quot; value=&quot;{{ search }}&quot; placeholder=&quot;{{ text_keyword }}&quot; id=&quot;input-search&quot; class=&quot;form-control&quot; /&gt;\n        &lt;/div&gt;\n        &lt;div class=&quot;col-sm-3&quot;&gt;\n          &lt;select name=&quot;category_id&quot; class=&quot;form-control&quot;&gt;\n            &lt;option value=&quot;0&quot;&gt;{{ text_category }}&lt;/option&gt;\n            {% for category_1 in categories %}\n            {% if category_1.category_id == category_id %}\n            &lt;option value=&quot;{{ category_1.category_id }}&quot; selected=&quot;selected&quot;&gt;{{ category_1.name }}&lt;/option&gt;\n            {% else %}\n            &lt;option value=&quot;{{ category_1.category_id }}&quot;&gt;{{ category_1.name }}&lt;/option&gt;\n            {% endif %}\n            {% for category_2 in category_1.children %}\n            {% if category_2.category_id == category_id %}\n            &lt;option value=&quot;{{ category_2.category_id }}&quot; selected=&quot;selected&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{{ category_2.name }}&lt;/option&gt;\n            {% else %}\n            &lt;option value=&quot;{{ category_2.category_id }}&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{{ category_2.name }}&lt;/option&gt;\n            {% endif %}\n            {% for category_3 in category_2.children %}\n            {% if category_3.category_id == category_id %}\n            &lt;option value=&quot;{{ category_3.category_id }}&quot; selected=&quot;selected&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{{ category_3.name }}&lt;/option&gt;\n            {% else %}\n            &lt;option value=&quot;{{ category_3.category_id }}&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{{ category_3.name }}&lt;/option&gt;\n            {% endif %}\n            {% endfor %}\n            {% endfor %}\n            {% endfor %}\n          &lt;/select&gt;\n        &lt;/div&gt;\n        &lt;div class=&quot;col-sm-3&quot;&gt;\n          &lt;label class=&quot;checkbox-inline&quot;&gt;\n            {% if sub_category %}\n            &lt;input type=&quot;checkbox&quot; name=&quot;sub_category&quot; value=&quot;1&quot; checked=&quot;checked&quot; /&gt;\n            {% else %}\n            &lt;input type=&quot;checkbox&quot; name=&quot;sub_category&quot; value=&quot;1&quot; /&gt;\n            {% endif %}\n            {{ text_sub_category }}&lt;/label&gt;\n        &lt;/div&gt;\n      &lt;/div&gt;\n      &lt;p&gt;&lt;br&gt;\n        &lt;label class=&quot;checkbox-inline&quot;&gt;\n          {% if description %}\n          &lt;input type=&quot;checkbox&quot; name=&quot;description&quot; value=&quot;1&quot; id=&quot;description&quot; checked=&quot;checked&quot; /&gt;\n          {% else %}\n          &lt;input type=&quot;checkbox&quot; name=&quot;description&quot; value=&quot;1&quot; id=&quot;description&quot; /&gt;\n          {% endif %}\n          {{ entry_description }}&lt;/label&gt;\n      &lt;/p&gt;&lt;br&gt;\n      &lt;input type=&quot;button&quot; value=&quot;{{ button_search }}&quot; id=&quot;button-search&quot; class=&quot;btn btn-primary&quot; /&gt;\n      &lt;h2 class=&quot;heading&quot;&gt;{{ text_search }}&lt;/h2&gt;\n      {% if products %}\n      &lt;div class=&quot;row cate-border&quot;&gt;\n            &lt;div class=&quot;col-md-2 col-sm-3 col-xs-3 catebt&quot;&gt;\n                &lt;div class=&quot;btn-group-sm&quot;&gt;\n                    &lt;button type=&quot;button&quot; id=&quot;list-view&quot; class=&quot;btn inslistgrid&quot; data-toggle=&quot;tooltip&quot; title=&quot;{{ button_list }}&quot;&gt;\n                       {#  &lt;i class=&quot;fa fa-th-list&quot;&gt;&lt;/i&gt; #} &lt;img src=&quot;image/catalog/category/list.png&quot;&gt;\n                    &lt;/button&gt;\n                    &lt;button type=&quot;button&quot; id=&quot;grid-view&quot; class=&quot;btn inslistgrid&quot; data-toggle=&quot;tooltip&quot; title=&quot;{{ button_grid }}&quot;&gt;\n                        {# &lt;i class=&quot;fa fa-th&quot;&gt;&lt;/i&gt; #} &lt;img src=&quot;image/catalog/category/grid.png&quot;&gt;\n                    &lt;/button&gt;\n                &lt;/div&gt;\n            &lt;/div&gt;\n            &lt;div class=&quot;col-lg-3 col-md-5 col-sm-6 col-xs-7 hidden-md hidden-sm hidden-xs ct&quot;&gt;\n                &lt;a href=&quot;{{ compare }}&quot; id=&quot;compare-total&quot; class=&quot;btn btn-link&quot;&gt;{{ text_compare }}&lt;/a&gt;\n            &lt;/div&gt;\n            &lt;div class=&quot;col-lg-4 col-md-5 col-xs-5 col-sm-5 sorting&quot;&gt;\n                &lt;div class=&quot;input-group input-group-sm&quot;&gt;\n                    &lt;label class=&quot;input-group-addon&quot; for=&quot;input-sort&quot;&gt;{{ text_sort }}&lt;/la