(53, 0, 'default', 'extension/module/ocsearchcategory/searchajaxresult', '{% if products|length == 0 %}\n    &lt;p class=&quot;ajax-result-msg&quot;&gt;{{ text_empty }}&lt;/p&gt;\n{% else %}\n    &lt;ul class=&quot;ajax-result-list&quot;&gt;\n        {% set i = 0 %}\n        {% for product in products %}\n            {% set i = i + 1 %}\n            &lt;li class=&quot;ajax-result-item&quot;&gt;\n                &lt;div class=&quot;product-info col-lg-6 col-md-12 col-sm-12 col-xs-12&quot;&gt;\n                    {% if product_img_enabled == 1 %}\n                        &lt;a class=&quot;product-image col-lg-4 col-sm-3 col-xs-4&quot; href=&quot;{{ product.href }}&quot;&gt;\n                            &lt;div class=&quot;product-image&quot;&gt;\n                                &lt;img src=&quot;{{ product.thumb }}&quot; alt=&quot;{{ product.name }}&quot; class=&quot;img-responsive center-block&quot; /&gt;\n                            &lt;/div&gt;\n                        &lt;/a&gt;\n                    {% endif %}\n                    &lt;div class=&quot;detail col-lg-8 col-sm-9 col-xs-8&quot;&gt;\n                        &lt;h2 class=&quot;product-name&quot;&gt;\n                            &lt;a href=&quot;{{ product.href }}&quot;&gt;{{ product.name }}&lt;/a&gt;\n                        &lt;/h2&gt;\n                        &lt;p class=&quot;list-des&quot;&gt;{{ product.description }}&lt;/p&gt;\n                        {% if product_price_enabled == 1 %}\n                            {% if product.price %}\n                            &lt;div class=&quot;price-box&quot;&gt;\n                                {% if not product.special %}\n                              		 {% if product.discount %}\n                                          &lt;span class=&quot;price-new&quot;&gt;{{ product.discount }}&lt;/span&gt; \n                                          &lt;span class=&quot;price-old&quot;&gt;{{ product.price }}&lt;/span&gt;\n                                      {%