(56, 0, 'default', 'extension/module/inspirequickview', '{{ header }}\n&lt;div id=&quot;product-product&quot; class=&quot;container product-quickview-detail&quot;&gt;\n   &lt;div id=&quot;page-preloader&quot; class=&quot;visible&quot;&gt;\n      &lt;div class=&quot;preloader&quot;&gt;\n          &lt;div id=&quot;loading-center-absolute&quot;&gt;\n                &lt;div class=&quot;object&quot; id=&quot;object_one&quot;&gt;&lt;/div&gt;\n          &lt;/div&gt;\n      &lt;/div&gt;\n  &lt;/div&gt; \n  &lt;div class=&quot;row&quot;&gt;{{ column_left }}\n    {% if column_left and column_right %}\n    {% set class = \'col-sm-6\' %}\n    {% elseif column_left or column_right %}\n    {% set class = \'col-sm-9\' %}\n    {% else %}\n    {% set class = \'col-sm-12\' %}\n    {% endif %}\n    &lt;div id=&quot;content&quot; class=&quot;{{ class }}&quot;&gt;\n      &lt;div class=&quot;row&quot;&gt; {% if column_left or column_right %}\n        {% set class = \'col-sm-6\' %}\n        {% else %}\n        {% set class = \'col-sm-6 col-md-6 col-xs-6 sticky\' %}\n        {% endif %}\n        &lt;div class=&quot;{{ class }}&quot;&gt; {% if thumb or images %}\n\n            &lt;div class=&quot;swiper-viewport&quot;&gt;\n              &lt;div id=&quot;carousel-product-images&quot; class=&quot;swiper-container&quot;&gt;\n                &lt;div class=&quot;swiper-wrapper&quot;&gt;{% for image in images %}\n                  &lt;div class=&quot;swiper-slide text-center&quot;&gt;&lt;img src=&quot;{{ image.popup }}&quot; title=&quot;{{ heading_title }}&quot; alt=&quot;{{ heading_title }}&quot; class=&quot;img-responsive center-block&quot; /&gt;&lt;/div&gt;\n                  {% endfor %}&lt;/div&gt;\n              &lt;/div&gt;\n              &lt;div class=&quot;swiper-pagination carousel-product-images show&quot;&gt;&lt;/div&gt;\n              &lt;div class=&quot;swiper-pager&quot;&gt;\n                &lt;div class=&quot;swiper-button-next&quot;&gt;&lt;/div&gt;\n                &lt;div class=&quot;swiper-button-prev&quot;&gt;&lt;/div&gt;\n              &lt;/div&gt;\n            &lt;/div&gt;\n             &lt;script type=&quot;text/javascript&quot;&gt;&lt;!--\n            $(\'#carousel-product-images\').swiper({\n              mode: \'horizontal\',\n              slidesPerView: 1,\n              pagination: false,\n              paginationClickable: true,\n              nextButton: \'.swiper-button-next\',\n                prevButton: \'.swiper-button-prev\',\n              autoplay: 2500,\n              loop: true\n            });\n            --&gt;&lt;/script&gt;\n         \n          {% endif %}\n        &lt;/div&gt;\n        {% if column_left or column_right %}\n        {% set class = \'col-sm-6\' %}\n        {% else %}\n        {% set class = \'col-sm-6 col-md-6 col-xs-6 pro-content\' %}\n        {% endif %}\n        &lt;div class=&quot;{{ class }} product-right&quot;&gt;\n          &lt;h1&gt;{{ heading_title }}&lt;/h1&gt;&lt;hr class=&quot;prosp&quot;&gt;\n          &lt;ul class=&quot;list-unstyled&quot;&gt;\n            {% if manufacturer %}\n            &lt;li&gt;&lt;span class=&quot;text-prodecor&quot;&gt;{{ text_manufacturer }}&lt;/span&gt;&lt;a href=&quot;{{ manufacturers }}&quot; class=&quot;textdeb&quot;&gt;{{ manufacturer }}&lt;/a&gt;&lt;/li&gt;\n            {% endif %}\n            &lt;li&gt;&lt;span class=&quot;text-prodecor&quot;&gt;{{ text_model }}&lt;/span&gt; {{ model }}&lt;/li&gt;\n            {% if reward %}\n            &lt;li&gt;&lt;span class=&quot;text-prodecor&quot;&gt;{{ text_reward }}&lt;/span&gt; {{ reward }}&lt;/li&gt;\n            {% endif %}\n            &lt;li&gt;&lt;span class=&quot;text-prodecor&quot;&gt;{{ text_stock }}&lt;/span&gt; {{ stock }}&lt;/li&gt;\n            &lt;hr class=&quot;prosp&quot;&gt;\n          &lt;/ul&gt;\n          {% if price %}\n          &lt;ul class=&quot;list-unstyled&quot;&gt;\n            {% if not special %}\n            &lt;li class=&quot;text-prodecor-bold&quot;&gt;\n              &lt;h2 class=&quot;pro-price&quot;&gt;{% if discount %}\n                    &lt;span &gt;{{ discount }}&lt;/span&gt; \n                    &lt;span style=&quot;color: #999;text-decoration: line-through;&quot;&gt;{{ price }}&lt;/span&gt;\n                {% else %} \n                    {{ price }} \n                {% endif %} &lt;/h2&gt;\n            &lt;/li&gt;\n            {% else %}\n            &lt;!-- &lt;li&gt;&lt;span style=&quot;text-decoration: line-through;&quot;&gt;{{ price }}&lt;/span&gt;&lt;/li&gt; --&gt;\n            &lt;li class=&quot;text-prodecor-bold&quot;&gt;\n              &lt;h2&gt;{{ special }}&lt;/h2&gt;\n            &lt;/li&gt;\n            {% endif %}\n            &lt;!-- {% if tax %}\n            &lt;li&gt;{{ text_tax }} {{ tax }}&lt;/li&gt;\n            {% endif %} --&gt;\n            {% if points %}\n            &lt;li class=&quot;text-prodecor-bold&quot;&gt;{{ text_points }} {{ points }}&lt;/li&gt;\n            {% endif %}\n            {% if discounts %}\n            &lt;li&gt;\n              &lt;hr class=&quot;prosp&quot;&gt;\n            &lt;/li&gt;\n            {% for discount in discounts %}\n            &lt;li&gt;{{ discount.quantity }}{{ text_discount }}{{ discount.price }}&lt;/li&gt;\n            {% endfor %}\n            {% endif %}\n          &lt;/ul&gt;\n          {% endif %}\n          &lt;div id=&quot;product&quot;&gt; {% if options %}\n            &lt;hr class=&quot;prosp&quot;&gt;\n            &lt;h3&gt;{{ text_option }}&lt;/h3&gt;\n            {% for option in options %}\n            {% if option.type == \'select\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-option{{ option.product_option_id }}&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;select name=&quot;option[{{ option.product_option_id }}]&quot; id=&quot;input-option{{ option.product_option_id }}&quot; class=&quot;form-control&quot;&gt;\n                &lt;option value=&quot;&quot;&gt;{{ text_select }}&lt;/option&gt;\n                {% for option_value in option.product_option_value %}\n                &lt;option value=&quot;{{ option_value.product_option_value_id }}&quot;&gt;{{ option_value.name }}\n                {% if option_value.price %}\n                ({{ option_value.price_prefix }}{{ option_value.price }})\n                {% endif %} &lt;/option&gt;\n                {% endfor %}\n              &lt;/select&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'radio\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;div id=&quot;input-option{{ option.product_option_id }}&quot; class=&quot;custom-radio&quot;&gt; {% for option_value in option.product_option_value %}\n                &lt;div class=&quot;radio&quot;&gt;\n                  &lt;label&gt;\n                    &lt;input type=&quot;radio&quot; name=&quot;option[{{ option.product_option_id }}]&quot; value=&quot;{{ option_value.product_option_value_id }}&quot; /&gt;\n                    {% if option_value.image %} &lt;img src=&quot;{{ option_value.image }}&quot; alt=&quot;{{ option_value.name }} {% if option_value.price %} {{ option_value.price_prefix }} {{ option_value.price }} {% endif %}&quot; class=&quot;img-thumbnail&quot; /&gt; {% endif %}\n                    {{ option_value.name }}\n                    {% if option_value.price %}\n                    ({{ option_value.price_prefix }}{{ option_value.price }})\n                    {% endif %} &lt;/label&gt;\n                &lt;/div&gt;\n                {% endfor %} &lt;/div&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'checkbox\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;div id=&quot;input-option{{ option.product_option_id }}&quot;&gt; {% for option_value in option.product_option_value %}\n                &lt;div class=&quot;checkbox&quot;&gt;\n                  &lt;label&gt;\n                    &lt;input type=&quot;checkbox&quot; name=&quot;option[{{ option.product_option_id }}][]&quot; value=&quot;{{ option_value.product_option_value_id }}&quot; /&gt;\n                    {% if option_value.image %} &lt;img src=&quot;{{ option_value.image }}&quot; alt=&quot;{{ option_value.name }} {% if option_value.price %} {{ option_value.price_prefix }} {{ option_value.price }} {% endif %}&quot; class=&quot;img-thumbnail&quot; /&gt; {% endif %}\n                    {{ option_value.name }}\n                    {% if option_value.price %}\n                    ({{ option_value.price_prefix }}{{ option_value.price }})\n                    {% endif %} &lt;/label&gt;\n                &lt;/div&gt;\n                {% endfor %} &lt;/div&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'text\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-option{{ option.product_option_id }}&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;input type=&quot;text&quot; name=&quot;option[{{ option.product_option_id }}]&quot; value=&quot;{{ option.value }}&quot; placeholder=&quot;{{ option.name }}&quot; id=&quot;input-option{{ option.product_option_id }}&quot; class=&quot;form-control&quot; /&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'textarea\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-option{{ option.product_option_id }}&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;textarea name=&quot;option[{{ option.product_option_id }}]&quot; rows=&quot;5&quot; placeholder=&quot;{{ option.name }}&quot; id=&quot;input-option{{ option.product_option_id }}&quot; class=&quot;form-control&quot;&gt;{{ option.value }}&lt;/textarea&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'file\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;button type=&quot;button&quot; id=&quot;button-upload{{ option.product_option_id }}&quot; data-loading-text=&quot;{{ text_loading }}&quot; class=&quot;btn btnupload btn-block&quot;&gt;&lt;i class=&quot;fa fa-upload&quot;&gt;&lt;/i&gt; {{ button_upload }}&lt;/button&gt;\n              &lt;input type=&quot;hidden&quot; name=&quot;option[{{ option.product_option_id }}]&quot; value=&quot;&quot; id=&quot;input-option{{ option.product_option_id }}&quot; /&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'date\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-option{{ option.product_option_id }}&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;div class=&quot;input-group date&quot;&gt;\n                &lt;input type=&quot;text&quot; name=&quot;option[{{ option.product_option_id }}]&quot; value=&quot;{{ option.value }}&quot; data-date-format=&quot;YYYY-MM-DD&quot; id=&quot;input-option{{ option.product_option_id }}&quot; class=&quot;form-control&quot; /&gt;\n                &lt;span class=&quot;input-group-btn&quot;&gt;\n                &lt;button class=&quot;btn datebtn&quot; type=&quot;button&quot;&gt;&lt;i class=&quot;fa fa-calendar&quot;&gt;&lt;/i&gt;&lt;/button&gt;\n                &lt;/span&gt;&lt;/div&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'datetime\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-option{{ option.product_option_id }}&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;div class=&quot;input-group datetime&quot;&gt;\n                &lt;input type=&quot;text&quot; name=&quot;option[{{ option.product_option_id }}]&quot; value=&quot;{{ option.value }}&quot; data-date-format=&quot;YYYY-MM-DD HH:mm&quot; id=&quot;input-option{{ option.product_option_id }}&quot; class=&quot;form-control&quot; /&gt;\n                &lt;span class=&quot;input-group-btn&quot;&gt;\n                &lt;button type=&quot;button&quot; class=&quot;btn datebtn&quot;&gt;&lt;i class=&quot;fa fa-calendar&quot;&gt;&lt;/i&gt;&lt;/button&gt;\n                &lt;/span&gt;&lt;/div&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% if option.type == \'time\' %}\n            &lt;div class=&quot;form-group{% if option.required %} required {% endif %}&quot;&gt;\n              &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-option{{ option.product_option_id }}&quot;&gt;{{ option.name }}&lt;/label&gt;\n              &lt;div class=&quot;input-group time&quot;&gt;\n                &lt;input type=&quot;text&quot; name=&quot;option[{{ option.product_option_id }}]&quot; value=&quot;{{ option.value }}&quot; data-date-format=&quot;HH:mm&quot; id=&quot;input-option{{ option.product_option_id }}&quot; class=&quot;form-control&quot; /&gt;\n                &lt;span class=&quot;input-group-btn&quot;&gt;\n                &lt;button type=&quot;button&quot; class=&quot;btn datebtn&quot;&gt;&lt;i class=&quot;fa fa-calendar&quot;&gt;&lt;/i&gt;&lt;/button&gt;\n                &lt;/span&gt;&lt;/div&gt;\n            &lt;/div&gt;\n            {% endif %}\n            {% endfor %}&lt;hr class=&quot;prosp&quot;&gt;\n            {% endif %}\n            {% if recurrings %}\n            &lt;hr class=&quot;prosp&quot;&gt;\n            &lt;h3&gt;{{ text_payment_recurring }}&lt;/h3&gt;\n            &lt;div class=&quot;form-group required&quot;&gt;\n              &lt;select name=&quot;recurring_id&quot; class=&quot;form-control&quot;&gt;\n                &lt;option value=&quot;&quot;&gt;{{ text_select }}&lt;/option&gt;\n                {% for recurring in recurrings %}\n                &lt;option value=&quot;{{ recurring.recurring_id }}&quot;&gt;{{ recurring.name }}&lt;/option&gt;\n                {% endfor %}\n              &lt;/select&gt;\n              &lt;div class=&quot;help-block&quot; id=&quot;recurring-description&quot;&gt;&lt;/div&gt;\n            &lt;/div&gt;\n            {% endif %}\n            &lt;!-- Quantity option --&gt;\n            &lt;div class=&quot;form-group&quot;&gt;\n            &lt;div class=&quot;row&quot;&gt;\n                &lt;div class=&quot;col-sm-2 col-md-1 col-xs-2 op-box qtlabel&quot;&gt;\n                    &lt;label class=&quot;control-label text-prodecorop&quot; for=&quot;input-quantity&quot;&gt;{{ entry_qty }}&lt;/label&gt;\n                &lt;/div&gt;\n              &lt;div class=&quot;col-md-11 col-sm-10 col-xs-10 op-box qty-plus-minus&quot;&gt;\n              &lt;button type=&quot;button&quot; class=&quot;form-control pull-left btn-number btnminus&quot; disabled=&quot;disabled&quot; data-type=&quot;minus&quot; data-field=&quot;quantity&quot;&gt;\n                  &lt;span class=&quot;glyphicon glyphicon-minus&quot;&gt;&lt;/span&gt;\n               &lt;/button&gt;\n              &lt;input id=&quot;input-quantity&quot; type=&quot;text&quot; name=&quot;quantity&quot; value=&quot;{{ minimum }}&quot; size=&quot;2&quot; id=&quot;input-quantity&quot; class=&quot;form-control input-number pull-left&quot; /&gt;\n              &lt;input type=&quot;hidden&quot; name=&quot;product_id&quot; value=&quot;{{ product_id }}&quot; /&gt;\n               &lt;button type=&quot;button&quot; class=&quot;form-control pull-left btn-number btnplus&quot; data-type=&quot;plus&quot; data-field=&quot;quantity&quot;&gt;\n                  &lt;span class=&quot;glyphicon glyphicon-plus&quot;&gt;&lt;/span&gt;\n               &lt;/button&gt;\n               &lt;/div&gt;\n               &lt;/div&gt;&lt;hr class=&quot;prosp&quot;&gt;\n              &lt;button type=&quot;button&quot; id=&quot;button-cart&quot; data-loading-text=&quot;{{ text_loading }}&quot; class=&quot;btn add-to-cart btn-primary&quot;&gt;{{ button_cart }}&lt;/button&gt;\n             &lt;button type=&quot;button&quot; data-toggle=&quot;tooltip&quot; title=&quot;{{ button_wishlist }}&quot; class=&quot;btn add-to-cart btn-primary&quot; onclick=&quot;wishlist.add(\'{{ product_id }}\');&quot;&gt;&lt;svg width=&quot;16px&quot; height=&quot;16px&quot;&gt;&lt;use xlink:href=&quot;#addwish&quot;&gt;&lt;/use&gt;&lt;/svg&gt;&lt;/button&gt;\n              &lt;button type=&quot;button&quot; data-toggle=&quot;tooltip&quot; title=&quot;{{ button_compare }}&quot; class=&quot;btn add-to-cart btn-primary&quot; onclick=&quot;compare.add(\'{{ product_id }}\');&quot; &gt;&lt;svg width=&quot;16px&quot; height=&quot;16px&quot;&gt;&lt;use xlink:href=&quot;#addcompare&quot;&gt;&lt;/use&gt;&lt;/svg&gt;&lt;/button&gt;\n              &lt;hr class=&quot;prosp&quot;&gt;\n            &lt;/div&gt;\n            &lt;!-- Quantity option end --&gt;\n            {% if minimum &gt; 1 %}\n            &lt;div class=&quot;alert alert-info palert&quot;&gt;&lt;i class=&quot;fa fa-info-circle&quot;&gt;&lt;/i&gt; {{ text_minimum }}&lt;/div&gt;\n            {% endif %}&lt;/div&gt;\n          {% if review_status %}\n          &lt;div class=&quot;rating&quot;&gt;\n            &lt;ul class=&quot;list-inline&quot;&gt;\n            &lt;li class=&quot;prodrate&quot;&gt;{% for i in 1..5 %}\n              {% if rating &lt; i %}&lt;span class=&quot;fa fa-stack&quot;&gt;&lt;i class=&quot;fa fa-star-o fa-stack-1x&quot;&gt;&lt;/i&gt;&lt;/span&gt;{% else %}&lt;span class=&quot;fa fa-stack&quot;&gt;&lt;i class=&quot;fa fa-star fa-stack-1x&quot;&gt;&lt;/i&gt;&lt;i class=&quot;fa fa-star-o fa-stack-1x&quot;&gt;&lt;/i&gt;&lt;/span&gt;{% endif %}\n              {% endfor %} &lt;/li&gt;\n              &lt;li class=&quot;proreviewre&quot;&gt;\n                &lt;a href=&quot;&quot; onclick=&quot;$(\'a[href=\\\'#tab-review\\\']\').trigger(\'click\'); return false;&quot;&gt;{{ reviews }}&lt;/a&gt;\n              &lt;/li&gt;\n              &lt;li class=&quot;prowritere&quot;&gt;\n                &lt;a href=&quot;#rt&quot; onclick=&quot;$(\'a[href=\\\'#tab-review\\\']\').trigger(\'click\'); return false;&quot;&gt;{{ text_write }}&lt;/a&gt;\n              &lt;/li&gt;\n              &lt;/ul&gt;\n            &lt;hr class=&quot;prosp&quot;&gt;\n             &lt;!-- AddToAny BEGIN --&gt;\n                        &lt;div class=&quot;a2a_kit a2a_kit_size_32 a2a_default_style&quot;&gt;\n                            &lt;a class=&quot;a2a_button_facebook&quot;&gt;&lt;/a&gt;\n                            &lt;a class=&quot;a2a_button_twitter&quot;&gt;&lt;/a&gt;\n                            &lt;a class=&quot;a2a_button_google_plus&quot;&gt;&lt;/a&gt;\n                            &lt;a class=&quot;a2a_button_pinterest&quot;&gt;&lt;/a&gt;\n                            &lt;a class=&quot;a2a_button_linkedin&quot;&gt;&lt;/a&gt;\n                            &lt;a class=&quot;a2a_dd&quot; href=&quot;https://www.addtoany.com/share&quot;&gt;&lt;/a&gt;\n                        &lt;/div&gt;\n                        &lt;script async src=&quot;https://static.addtoany.com/menu/page.js&quot;&gt;&lt;/script&gt;\n            &lt;!-- AddToAny END --&gt;\n          &lt;/div&gt;\n          {% endif %} \n        &lt;/div&gt;\n      &lt;/div&gt;\n        {% if tags %}\n        &lt;p&gt;{{ text_tags }}\n        {% for i in 0..tags|length %}\n        {% if i &lt; (tags|length - 1) %} &lt;a href=&quot;{{ tags[i].href }}&quot;&gt;{{ tags[i].tag }}&lt;/a&gt;,\n        {% else %} &lt;a href=&quot;{{ tags[i].href }}&quot;&gt;{{ tags[i].tag }}&lt;/a&gt; {% endif %}\n        {% endfor %} &lt;/p&gt;\n        {% endif %}\n      {{ content_bottom }}&lt;/div&gt;\n    &lt;/div&gt;\n&lt;/div&gt;\n&lt;script type=&quot;text/javascript&quot;&gt;&lt;!--\n$(\'select[name=\\\'recurring_id\\\'], input[name=&quot;quantity&quot;]\').change(function(){\n  $.ajax({\n    url: \'index.php?route=product/product/getRecurringDescription\',\n    type: \'post\',\n    data: $(\'input[name=\\\'product_id\\\'], input[name=\\\'quantity\\\'], select[name=\\\'recurring_id\\\']\'),\n    dataType: \'json\',\n    beforeSend: function() {\n      $(\'#recurring-description\').html(\'\');\n    },\n    success: function(json) {\n      $(\'.alert-dismissible, .text-danger\').remove();\n\n      if (json[\'success\']) {\n        $(\'#recurring-description\').html(json[\'success\']);\n      }\n    }\n  });\n});\n//--&gt;&lt;/script&gt;\n&lt;script type=&quot;text/javascript&quot;&gt;&lt;!--\n$(\'#button-cart\').on(\'click\', function() {\n  $.ajax({\n    url: \'index.php?route=checkout/cart/add\',\n    type: \'post\',\n    data: $(\'#product input[type=\\\'text\\\'], #product input[type=\\\'hidden\\\'], #product input[type=\\\'radio\\\']:checked, #product input[type=\\\'checkbox\\\']:checked, #product select, #product textarea\'),\n    dataType: \'json\',\n    beforeSend: function() {\n      $(\'#button-cart\').button(\'loading\');\n    },\n    complete: function() {\n      $(\'#button-cart\').button(\'reset\');\n    },\n    success: function(json) {\n      $(\'.alert-dismissible, .text-danger\', window.parent.document ).remove();\n      $(\'.form-group\').removeClass(\'has-error\');\n\n      if (json[\'error\']) {\n        if (json[\'error\'][\'option\']) {\n          for (i in json[\'error\'][\'option\']) {\n            var element = $(\'#input-option\' + i.replace(\'_\', \'-\'));\n\n            if (element.parent().hasClass(\'input-group\')) {\n              element.parent().after(\'&lt;div class=&quot;text-danger&quot;&gt;\' + json[\'error\'][\'option\'][i] + \'&lt;/div&gt;\');\n            } else {\n              element.after(\'&lt;div class=&quot;text-danger&quot;&gt;\' + json[\'error\'][\'option\'][i] + \'&lt;/div&gt;\');\n            }\n          }\n        }\n\n        if (json[\'error\'][\'recurring\']) {\n          $(\'select[name=\\\'recurring_id\\\']\').after(\'&lt;div class=&quot;text-danger&quot;&gt;\' + json[\'error\'][\'recurring\'] + \'&lt;/div&gt;\');\n        }\n\n        // Highlight any found errors\n        $(\'.text-danger\').parent().addClass(\'has-error\');\n      }\n\n      if (json[\'success\']) {\n        $(\'.breadcrumb\', window.parent.document ).after(\'&lt;div class=&quot;alert alert-success alert-dismissible&quot;&gt;\' + json[\'success\'] + \'&lt;button type=&quot;button&quot; class=&quot;close&quot; data-dismiss=&quot;alert&quot;&gt;&amp;times;&lt;/button&gt;&lt;/div&gt;\');\n\n        $(\'#cart &gt; button\', window.parent.document ).html(\'&lt;img src=&quot;image/catalog/carti.png&quot;/&gt;&lt;div class=&quot;cartco&quot;&gt;&lt;span&gt;My cart&lt;/span&gt;&lt;br&gt;&lt;span id=&quot;cart-total&quot;&gt;\' + json[\'total\'] + \'&lt;/span&gt;&lt;/div&gt;\');\n\n        $(\'html, body\',  window.parent.document ).animate({ scrollTop: 0 }, \'slow\');\n\n        $(\'#cart &gt; ul\', window.parent.document ).load(\'index.php?route=common/cart/info ul li\');\n         window.parent.$.magnificPopup.close();\n      }\n    },\n        error: function(xhr, ajaxOptions, thrownError) {\n            alert(thrownError + &quot;\\r\\n&quot; + xhr.statusText + &quot;\\r\\n&quot; + xhr.responseText);\n        }\n  });\n});\n//--&gt;&lt;/script&gt;\n&lt;script type=&quot;text/javasc